mapper.select:
SELECT catalog_id catalogId, catalog_name catalogName, parent_id parentId,
"rank"
FROM catalog_info WHERE class_name = #{className}

SELECT catalog_id catalogId, catalog_name catalogName, parent_id parentId,
"rank"
FROM catalog_info WHERE catalog_id = #{catalogId}

mapper.update:
UPDATE catalog_info a SET
	update_time=now()

		,a.catalog_name = #{catalogName}


		,a.parent_id = #{parentId}



			,a."rank" = #{rank}				 


WHERE a.catalog_id = #{catalogId}
